INSERT INTO `proposal_details` VALUES (32939, 10680, NULL, 3, NULL, NULL, 1, 6196, NULL, NULL, 8662.48, 0, 2960.00, 'Repairs', 'Repairs', 0, 5, 0, 2300, 256, 0, 29, 0, 2, 2, 334, 0, 96, 343, 18, 0, 0, 18, 4.4, 0.15, 0, 43, 0, '<p>Saw-cut and remove damaged asphalt where necessary&nbsp;<br>Properly dispose of any removed materials&nbsp;<br>Preparation and cleaning of surface area, including grading&nbsp;<br>Apply DOT approved tack coat to surface which ensures asphalt adheres to surface&nbsp;<br>Apply up to @@INCHES@@\" of compacted DOT approved hot mix asphalt<br>Compact with a 3-5 ton vibratory roller&nbsp;<br>Complete tie-ins to existing asphalt to create a smooth transition<br><br></p>', NULL, NULL, NULL, 10, NULL, NULL, NULL, NULL, NULL, NULL, '2023-06-18 19:18:11');
