INSERT INTO `proposal_details` VALUES (32942, 10680, NULL, 6, NULL, NULL, 1, 6196, NULL, NULL, 622.00, NULL, 0.00, 'Curb (Extruded)', 'Curb (Extruded)', 0, 7, 3434, 0, 0, 58, 0, 0, 3, 0, 33, 0, 0, 354, 0, 0, 0, 0, 0, 0, 0, 55, 0, '<p>Saw-cut and remove existing curbing where necessary<br>Properly dispose of any removed materials<br>Form and pour Type EXT 6\" x 6\" curbing using 3000 psi concrete<br>Hand-finish curb edges&nbsp;<br>Strip forms and clean once concrete cures</p>', NULL, NULL, NULL, 10, NULL, NULL, NULL, NULL, NULL, NULL, '2023-06-18 19:11:17');
