INSERT INTO `proposal_details` VALUES (32951, 10680, NULL, 15, NULL, NULL, 1, 6196, NULL, NULL, 2785.00, 0, 547.00, 'Sealcoating', 'Sealcoating', 0, 15, 0, 1211, 0, 0, 0, 0, 0, 0, 332, 0, 0, 32, 22, 120, 5, 1, 11, 22, 4, 826, 0, '<p>Thoroughly clean entire area to be sealed with blowers and cleaning machinery. Oil-stained spots will be sealed with a petro-bond (provides better adhesion through separation of oil spot) The area under consideration is up to @1211 SQ FT We will apply 2 spray coats of long lasting rubber-fortified StarSeal Pavement Sealer Sealcoat is strictly manufactured to Federal specifications (GSA-FSS) RP335D, with 3 pounds of Silica Sand per gallon with 2% Polymer Additive and applied at the rate of 1/8 gallon per square yard This project is proposed for @[object HTMLInputElement] phases (Additional phases available for additional cost)</p>', NULL, NULL, NULL, 10, NULL, NULL, NULL, NULL, NULL, NULL, '2023-06-18 19:11:17');
INSERT INTO `proposal_details` VALUES (32952, 10680, NULL, 16, NULL, NULL, 1, 6196, NULL, NULL, 2489.00, 0, 0.00, 'Other Service', 'Other Service', 0, 16, 0, 0, 0, 0, 0, 0, 2, 0, 100, 0, 2344, 22, 0, 0, 0, 0, 0, 0, 0, 23, 0, '<p>Two flag-men on-site to coordinate traffic and trucking<br>Project coordinating, phasing, and on-site support<br>Work schedules and phasing maps for residents for the project<br>Pre-Construction meeting with Property Manager and any interested board members<br>Designated Staging Area for equipment on-site<br>Provide signs, barricades, and cones as required for safety<br>Provide sand to protect area from tire tracking.</p>', 'test description here', NULL, NULL, 10, NULL, NULL, NULL, NULL, NULL, NULL, '2023-06-18 19:11:17');
