INSERT INTO `proposal_details` VALUES (32955, 10680, NULL, 19, NULL, NULL, 1, 6196, NULL, NULL, 5353.00, 0, 4646.00, 'Milling', 'Milling', 0, 19, 0, 1233, 137, 0, 0, 1, 2, 2, 33, 2, 2323, 33, 0, 0, 0, 0, 0, 0, 0, 33, 0, '<p>Mill existing asphalt @@SQFT@@ sq. ft.<br>Clean surface with a box sweeper to collect millings<br>Haul away asphalt millings<br>Clean surface for Asphalt Paving<br>Trim edges of roadway with a milling head to ensure flush removal of asphalt<br>Price includes two flagmen on-site to coordinate trucking and traffic for neighborhood residents</p>', NULL, NULL, NULL, 10, NULL, NULL, NULL, NULL, NULL, NULL, '2023-06-18 19:11:17');
INSERT INTO `proposal_details` VALUES (32956, 10680, NULL, 20, NULL, NULL, 1, 6196, NULL, NULL, 34463.00, 0, 34343.00, 'Paver Brick', 'Paver Brick', 0, 20, 0, 2344, 0, 0, 3, 0, 0, 0, 43, 0, 34343, 34, 0, 0, 0, 0, 0, 0, 0, 43, 0, '<p>Excavate and remove up to@3tons of existing materials<br>Demo and remove existing paver bricks<br>Lift existing paver bricks and set aside<br>Apply new sand and compact ready for pavers<br>Re-sand paver bricks to set in place using (polymeric/regular) sand<br>Pressure wash paver bricks<br>Seal paver bricks using Cobble Loc sealer<br>Furnish and install @2344 Sq Ft. of paver bricks<br>Remove existing paver bricks and re-sand</p>', '[object Object]', NULL, NULL, 10, NULL, NULL, NULL, NULL, NULL, NULL, '2023-06-18 19:11:17');
INSERT INTO `proposal_details` VALUES (32957, 10680, NULL, 21, NULL, NULL, 1, 6196, NULL, NULL, 3534.00, 0, 3434.00, 'Drainage and Catchbasins', 'Drainage and Catchbasins', 0, 21, 0, 0, 0, 0, 0, 0, 0, 0, 34, 0, 3434, 33, 0, 0, 0, 0, 0, 0, 0, 33, 34, '<p>Furnish and Install @444 Type C Catch Basins.<br>Perform asphalt repairs necessary to tie catch basin and collar into existing asphalt.<br>Saw-cut existing concrete structure.<br>Remove 1 brick from structure to lower grate 2 inches.<br>Re-place structure lid on new lowered base.<br>Use concrete mortar to lock lid in place.<br>Back-fill around structure.<br>Re-check all elevations to make sure drainage structure is on proper grade.</p>', 'test desc', NULL, NULL, 10, NULL, NULL, NULL, NULL, NULL, NULL, '2023-06-18 20:03:48');
