INSERT INTO `services` (`id`, `service_category_id`, `name`, `description`, `service_template`, `service_text_en`, `service_text_es`) VALUES (19, 1, 'Milling', 'na', 'Mill existing asphalt #SQFT# sq. ft.<br>Clean surface with a box sweeper to collect millings<br>Haul away asphalt millings<br>Clean surface for Asphalt Paving<br>Trim edges of roadway with a milling head to ensure flush removal of asphalt<br>Price includes two flagmen on-site to coordinate trucking and traffic for neighborhood residents', 'Mill existing asphalt down desired depth<br>Clean surface with box sweeper, collect millings<br>Haul off and properly dispose of millings<br>Trim edges with milling head for flush removal of asphalt<br><br>', 'Fresar o perfilar el asfalto existente hasta la profundidad deseada<br>Limpie la superficie con el box sweeper y recoja todo el fresado<br>Arrojar apropiadamente todo el fresado<br>Recortar los bordes con el cabezal de la fresadora para que el asfalto quede nivelado<br><br>');
INSERT INTO `services` (`id`, `service_category_id`, `name`, `description`, `service_template`, `service_text_en`, `service_text_es`) VALUES (20, 6, 'Paver Brick', 'na', 'Excavate and remove up to#TONS#tons of existing materials<br>Demo and remove existing paver bricks<br>Lift existing paver bricks and set aside<br>Apply new sand and compact ready for pavers<br>Re-sand paver bricks to set in place using (polymeric/regular) sand<br>Pressure wash paver bricks<br>Seal paver bricks using Cobble Loc sealer<br>Furnish and install #SQFT# Sq Ft. of paver bricks<br>Remove existing paver bricks and re-sand', 'Excavate and remove existing materials OR<br>Demo and remove existing paver bricks&nbsp;<br>Furnish and install new paver bricks<br><br>OR<br>Lift existing paver bricks and set aside&nbsp;<br>Apply new sand and compact ready for pavers<br>Re-sand paver bricks to set in place using (polymeric/regular) sand<br><br>AND/OR<br>Pressure wash paver bricks<br><br>AND/OR<br>Seal paver bricks using Cobble Loc sealer<br><br><br>', 'Excavar y remover los materiales existentes O&nbsp;<br>Demoler y remover los adoquines existentes<br>Proveer e instalar adoquines nuevos<br><br>O<br>Quitar los adoquines existentes y colocar a un lado<br>Aplicar arena nueva y compactar listo para los adoquines<br>Agregar arena sobre los adoquines para asentarlos usando arena (polimerica/regular)<br><br>Y/O<br>Lavar a presion los adoquines<br><br>Y/O<br>Sellar los adoquines usando Cobble Loc');
INSERT INTO `services` (`id`, `service_category_id`, `name`, `description`, `service_template`, `service_text_en`, `service_text_es`) VALUES (21, 3, 'Drainage and Catchbasins', 'Drainage and Catchbasins', 'Furnish and Install #BASINS# Type C Catch Basins.<br>Perform asphalt repairs necessary to tie catch basin and collar into existing asphalt.<br>Saw-cut existing concrete structure.<br>Remove 1 brick from structure to lower grate 2 inches.<br>Re-place structure lid on new lowered base.<br>Use concrete mortar to lock lid in place.<br>Back-fill around structure.<br>Re-check all elevations to make sure drainage structure is on proper grade.', 'Furnish and install Type C Basins AND<br>Perform asphalt repairs necessary around Basins<br><br>OR<br><br>Saw-cut existing structure<br>Lower basin to achieve proper drainage<br>Mortar lid back into place<br>Re-check elevations to ensure proper grade', 'No se olvide de traer su pala');
INSERT INTO `services` (`id`, `service_category_id`, `name`, `description`, `service_template`, `service_text_en`, `service_text_es`) VALUES (22, 1, 'Milling and Paving- (Over 3500 SY)', 'Asphalt overlay', '<strong>ASPHALT MILLING :<br></strong><br>Mill existing asphalt 1 inches<br>Clean surface with a box sweeper to collect millings<br>Haul away asphalt millings<br>Clean surface for Asphalt Paving<br>Trim edges of roadway with a milling head to ensure flush removal of asphalt<br>Price includes two flagmen on-site to coordinate trucking and traffic for neighborhood residents<br><br><strong>ASPHALT PAVING:<br></strong><br>Preparation and cleaning of surface area<br>Apply D.O.T. approved tack coat to surface<br>Apply 1.25 inches of D.O.T. approved S-3 hot mix asphalt<br>Asphalt will be 1 inches after compaction with a 3-5 ton vibratory roller.&nbsp;&nbsp;<br>Re-roll with an asphalt tire (rubber) roller<br>Price includes two flagmen on-site to coordinate trucking and traffic for neighborhood residents', '<strong>ASPHALT MILLING</strong><br>Mill existing asphalt to desired depth<br>Clean surface with a box sweeper to collect millings<br>Haul away asphalt millings<br>Clean surface for Asphalt Paving<br>Trim edges of roadway with a milling head<br><strong>ASPHALT PAVING</strong>&nbsp;Preparation and cleaning of surface area<br>Apply D.O.T. approved tack coat to surface<br>Apply D.O.T. approved S-3 hot mix asphalt<br>Asphalt will be desired depth after compaction with a 3-5 ton vibratory roller.', '<strong>FRESADO DE ASFALTO<br></strong><br>Fresar o perfilar el asfalto existente hasta la profundidad deseada<br>Limpie la superficie con el box sweeper y recoja todo el fresado<br>Arrojar apropiadamente todo el fresado<br>Limpie la superficie para pavimentar con asfalto<br>Recortar los bordes con el cabezal de la fresadora&nbsp;<br><br><strong>PAVIMENTO DE ASFALTO<br></strong><br>Preparacion y limpieza de la superficie<br>Aplique una capa de tack aprobado por el DOT a la superficie<br>Aplique asfalto caliente S-3 aprobado por el DOT<br>El Asfalto tendra el grosor deseado luego de compactar con un rodillo vibratorio de 3-5 toneladas<br><br><br>');
